mapper.select:
select stu.id, stu.name, stu.phone, stu.remark, stu.create_time, stu.update_time, stu.operator, stu.status, dict2.name status_name, stu.next_visit_date, stu.level, dict1.name level_name, user1.name operator_name, stu.current_operator, user2.name current_operator_name, stu.type, stu.sex, sex.name sex_name from student_infos stu left JOIN user user1 on stu.operator = user1.id left JOIN user user2 on stu.current_operator = user2.id left JOIN (select num,name from dict where pid in(select id from dict where dic_code='level')) dict1 on stu.level = dict1.num left JOIN (select num,name from dict where pid in(select id from dict where dic_code='signUp')) dict2 on stu.status = dict2.num left JOIN (select num,name from dict where pid in(select id from dict where dic_code='sex')) sex on stu.status = sex.num where ( (CASE when (SELECT sum(data_authority) FROM role_data_authority WHERE role_id IN ( SELECT roleid FROM USER WHERE id = #{currentOperator} )) > 0 then 1=1 else current_operator=#{currentOperator} end ))
and stu.name = #{name}
and stu.phone = #{phone}
and DATE_FORMAT(stu.create_time,'%Y-%m-%d')= DATE_FORMAT(#{createTime},'%Y-%m-%d')
and DATE_FORMAT(stu.next_visit_date,'%Y-%m-%d')= DATE_FORMAT(#{nextVisitDate},'%Y-%m-%d')
and user2.name = #{currentOperatorName}
order by stu.create_time desc
